import React, { Component } from 'react'

export class Form extends Component {
  state = {
    name: '',
    email: '',
    message: '',
    select: '',
    checked: false,
    file: '',
  }

  genericStates = ({ target: { name, type, checked, file, value } }) => {
    if (type === checked) {
      this.setState({ [name]: value })
    } else if (type === file) {
      this.setState({ [name]: value })
    } else {
      this.setState({ [name]: value })
    }
  };
  // captureEmail = ({target}) => {
  //   this.setState({ email: target.value})
  // }
  // captureMessage = ({target}) => {
  //   this.setState({ message: target.value})
  // }

  render() {
    return (
      <form>
        <fieldset>
          <label htmlFor="">
            Name:
            <input type="text" name="name" onChange={this.genericStates} />
          </label>

          <label htmlFor="">
            Email:
            <input type="email" name="email" onChange={this.genericStates} />
          </label>
        </fieldset>

        <label htmlFor="">
          <textarea name="message" id="" cols="30" rows="10" onChange={this.genericStates} ></textarea>
        </label>

        <label htmlFor="">
          Insira seu Arquivo:
          <input type="file" name="file" id="" />
        </label>

        <select name="select" id="" onChange={this.genericStates}>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
        </select>

        <input type="checkbox" name="checked" id="" onChange={this.genericStates} />
      </form>
    )
  }
}

export default Form
