import React, { Component } from 'react'

export class Form extends Component {
  state = {
    name: '',
    email: '',
    message: '',
  }

  captureName = ({target}) => {
    this.setState({ name: target.value})
  }
  captureEmail = ({target}) => {
    this.setState({ email: target.value})
  }
  captureMessage = ({target}) => {
    this.setState({ message: target.value})
  }

  render() {
    return (
      <form>
          <label htmlFor="">
            Name:
            <input type="text" onChange={ this.captureName } />
          </label>

          <label htmlFor="">
            Email:
            <input type="email" onChange={ this.captureEmail } />
          </label>

          <label htmlFor="">
            <textarea name="" id="" cols="30" rows="10" onChange={ this.captureMessage } ></textarea>
          </label>

          <select name="" id="">
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
          </select>
      </form>
    )
  }
}

export default Form
