import React, { Component } from 'react'

export class Form extends Component {
  state = {
    name: '',
    email: '',
    message: '',
    select: '',
  }

  genericStates = ({ target: { name, type, checked, value } }) => {
    const values = type === 'checkbox' ? checked : value;
    this.setState({ [name]: values})
  };
  // captureEmail = ({target}) => {
  //   this.setState({ email: target.value})
  // }
  // captureMessage = ({target}) => {
  //   this.setState({ message: target.value})
  // }

  render() {
    return (
      <form>
          <label htmlFor="">
            Name:
            <input type="text" name="name" onChange={ this.genericStates } />
          </label>

          <label htmlFor="">
            Email:
            <input type="email"  name="email" onChange={ this.genericStates } />
          </label>

          <label htmlFor="">
            <textarea name="message" id="" cols="30" rows="10" onChange={ this.genericStates } ></textarea>
          </label>

          <select name="select" id="" onChange={ this.genericStates }>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
          </select>
      </form>
    )
  }
}

export default Form
