// App.js
import React, { Component } from 'react';
import './App.css';

class App extends Component {

    state = {
        characters: [],
    }

    requestPhotosRickAndMortin  = async () => {
      const { characters } = this.state;
      const resquestApi = await fetch('https://rickandmortyapi.com/api/character');
      const resultRequest = await resquestApi.json();
      this.setState({characters: [...characters, resultRequest]})
    }


  componentDidMount() {
  //   fetch('https://rickandmortyapi.com/api/character')
  //   .then(response => response.json())
  //   .then(data => {
  //     this.setState({characters: data.results})
  //   })
    this.requestPhotosRickAndMortin();
  }

  render() {
    const { characters } = this.state;
    const load = <p>loading...</p>
    console.log(characters);
    return (
      <div className="App">
        <h1>
          Ricky and Morty Characters:
        </h1>
        <div className="body">
          {characters.length > 0 ? characters.map(({ id, image, name }) => {
            return (
              <div className="container" key={id}>
                <h3>{id}</h3>
                <img src={image} alt={name}/>
              </div>
            )
          }): load}
        </div>
      </div>
    );
  }
}

export default App;
